import React, { useEffect, useState } from "react";
import { getUserOrders, getCurrentUserId, cancelOrder } from "../services/api";
import { useNavigate } from "react-router-dom";

const MyOrders = () => {
  const [orders, setOrders] = useState([]);
  const [error, setError] = useState("");
  const navigate = useNavigate();

  useEffect(() => {
    const fetchOrders = async () => {
      try {
        const userId = getCurrentUserId();
        const response = await getUserOrders(userId);
        setOrders(response.data);
      } catch (err) {
        setError("❌ Error fetching orders: " + err.message);
      }
    };

    fetchOrders();
  }, []);

  const handleCancelOrder = async (orderId) => {
    try {
      await cancelOrder(orderId);
      setOrders((prevOrders) =>
        prevOrders.filter((order) => order.orderId !== orderId)
      );
    } catch (err) {
      setError("❌ Error canceling order: " + err.message);
    }
  };

  return (
    <div className="min-h-screen bg-gray-100 p-6">
      <button
        className="mb-4 px-4 py-2 bg-blue-500 text-white rounded"
        onClick={() => navigate("/dashboard")}
      >
        🔙 Back to Dashboard
      </button>

      <h1 className="text-3xl font-bold mb-6 text-center">📄 My Orders</h1>

      {error && <p className="text-red-600 text-center mb-4">{error}</p>}

      {orders.length === 0 ? (
        <p className="text-center text-gray-600">No orders found.</p>
      ) : (
        <div className="grid md:grid-cols-2 lg:grid-cols-3 gap-6">
          {orders.map((order) => (
            <div
              key={order.orderId}
              className="bg-white shadow-md rounded p-4 space-y-2"
            >
              <div className="flex justify-between items-center">
                <h2 className="text-lg font-bold">Order #{order.orderId}</h2>
                <span className="text-sm text-gray-600">
                  {new Date(order.createdAt).toLocaleDateString()}
                </span>
              </div>

              <p className="text-sm">
                💰 Total: <span className="font-semibold">${order.totalPrice.toFixed(2)}</span>
              </p>
              <p className="text-sm">
                📝 Status:{" "}
                <span className="font-semibold text-blue-600">{order.status.name}</span>
              </p>

              {/* Order Items */}
              {order.items && order.items.length > 0 && (
                <div className="mt-2">
                  <p className="text-sm font-semibold mb-1">🧾 Items:</p>
                  <ul className="pl-4 list-disc text-sm text-gray-700 space-y-1">
                    {order.items.map((item, idx) => (
                      <li key={idx}>
                        {item.product.name} × {item.quantity} = $
                        {(item.product.price * item.quantity).toFixed(2)}
                      </li>
                    ))}
                  </ul>
                </div>
              )}

              {/* Cancel button */}
              {order.status.name === "Pending" && (
                <button
                  onClick={() => handleCancelOrder(order.orderId)}
                  className="mt-4 w-full bg-red-500 hover:bg-red-600 text-white py-2 rounded"
                >
                  ❌ Cancel Order
                </button>
              )}
            </div>
          ))}
        </div>
      )}
    </div>
  );
};

export default MyOrders;
