import React, { useEffect, useState } from "react";
import { getAvailableProducts, createOrder, getCurrentUserId } from "../services/api";
import axios from "axios";
import { useNavigate } from "react-router-dom";


const OrderNow = () => {
  const [products, setProducts] = useState([]);
  const [selected, setSelected] = useState({});
  const [message, setMessage] = useState("");

  useEffect(() => {
    getAvailableProducts()
      .then((res) => setProducts(res.data))
      .catch((err) => console.error("Error fetching products:", err));
  }, []);

  const handleQuantityChange = (productId, quantity) => {
    setSelected((prev) => ({
      ...prev,
      [productId]: Number(quantity),
    }));
  };

  const handleOrderSubmit = async () => {
    const items = Object.entries(selected)
      .filter(([_, qty]) => qty > 0)
      .map(([productId, qty]) => ({
        productId: Number(productId),
        quantity: qty,
      }));

    if (items.length === 0) {
      setMessage("‚ùå You must select at least one product.");
      return;
    }

    const totalPrice = items.reduce((acc, item) => {
      const product = products.find((p) => p.productId === item.productId);
      return acc + (product?.price || 0) * item.quantity;
    }, 0);

    try {
      const userId = getCurrentUserId();
      const orderDTO = { userId, totalPrice }; // Create order payload
      const orderRes = await createOrder(orderDTO); // Call createOrder API
      const orderId = orderRes.data.orderId;

      // Create order items
      const orderItemRequests = items.map((item) => ({
        orderId,
        productId: item.productId,
        quantity: item.quantity,
      }));

      // Send order items to the API
      for (const orderItem of orderItemRequests) {
        await axios.post("http://localhost:8080/orderItems", orderItem);
      }

      setMessage("‚úÖ Order placed successfully!");
      setSelected({});
    } catch (error) {
      console.error(error);
      setMessage("‚ùå Failed to submit order.");
    }
  };

  return (
    <div className="min-h-screen bg-gray-50 p-6">
      <h1 className="text-3xl font-bold mb-4 text-center">üõí Order Now</h1>

      {message && <p className="text-center mb-4 text-blue-700 font-semibold">{message}</p>}

      <div className="grid md:grid-cols-2 lg:grid-cols-3 gap-4">
        {products.map((product) => (
          <div key={product.productId} className="bg-white p-4 rounded shadow">
            <h2 className="text-lg font-semibold">{product.name}</h2>
            <p className="text-sm text-gray-600">{product.description}</p>
            <p className="text-sm text-red-600">Stock: {product.stockQuantity}</p>
            <p className="text-blue-600 font-bold mt-2">${product.price.toFixed(2)}</p>

            <input
              type="number"
              min={0}
              className="mt-2 w-full border p-2 rounded"
              value={selected[product.productId] || ""}
              onChange={(e) => handleQuantityChange(product.productId, e.target.value)}
              placeholder="Enter quantity"
            />
          </div>
        ))}
      </div>

      <div className="mt-6 text-center">
        <button
          onClick={handleOrderSubmit}
          className="bg-green-500 hover:bg-green-600 text-white px-6 py-2 rounded"
        >
          ‚úÖ Confirm Order
        </button>
      </div>
    </div>
  );
};

export default OrderNow;
