import React, { useEffect, useState } from "react";
import axios from "axios";
import { useNavigate } from "react-router-dom";

const ProductCatalogAdmin = () => {
  const navigate = useNavigate();
  const [products, setProducts] = useState([]);
  const [form, setForm] = useState({});
  const [message, setMessage] = useState("");
  const [updateId, setUpdateId] = useState("");
  const [updateQuantity, setUpdateQuantity] = useState("");

  const API = "http://localhost:8080/admin/products";

  useEffect(() => {
    fetchProducts();    
  }, []);

  const fetchProducts = async () => {
    try {
      const res = await axios.get(API);
      setProducts(res.data);
    } catch {
      setMessage("‚ùå Failed to load products.");
    }
  };

  const handleInput = (e) => {
    setForm((prev) => ({ ...prev, [e.target.name]: e.target.value }));
  };

  const handleCreate = async () => {
    const today = new Date();
    const availableUntilDate = new Date(form.availableUntil);
  
    // Osnovna validacija
    if (!form.name || !form.description || !form.price || !form.availableUntil || !form.stockQuantity) {
      setMessage("‚ùå Please fill in all fields.");
      return;
    }
  
    if (availableUntilDate <= today) {
      setMessage("‚ùå 'Available Until' must be a future date.");
      return;
    }
  
    try {
      await axios.post(API, {
        catalog: { catalogId: 4 },
        name: form.name,
        description: form.description,
        price: parseFloat(form.price),
        availableUntil: form.availableUntil,
        stockQuantity: parseInt(form.stockQuantity),
      });
  
      setForm({});
      setMessage("‚úÖ Product created.");
      fetchProducts();
    } catch (err) {
      console.error(err);
      setMessage("‚ùå Failed to create product.");
    }
  };
  
  

  const handleDelete = async (productId) => {
    if (!window.confirm("Are you sure you want to delete this product?")) return;
    try {
      await axios.delete(`${API}/${productId}`);
      setMessage("‚úÖ Product removed.");
      fetchProducts();
    } catch {
      setMessage("‚ùå Failed to delete product.");
    }
  };

  const handleUpdateStock = async () => {
    if (!updateId || !updateQuantity) {
      setMessage("‚ùå Please enter both Product ID and Quantity.");
      return;
    }
  
    try {
      await axios.patch(`${API}/${updateId}/quantity`, null, {
        params: {
          quantity: updateQuantity,
        },
      });
      setMessage("‚úÖ Stock updated.");
      setUpdateId("");
      setUpdateQuantity("");
      fetchProducts();
    } catch {
      setMessage("‚ùå Failed to update stock.");
    }
  };
  

  return (
    <div className="p-6 min-h-screen bg-gray-50">
      <button
        className="mb-6 px-4 py-2 bg-blue-500 text-white rounded"
        onClick={() => navigate("/admin")}
      >
        üîô Back to Admin Dashboard
      </button>

      <h1 className="text-3xl font-bold mb-4">üì¶ Product Catalog Admin</h1>

      {message && <p className="mb-4 text-blue-700">{message}</p>}

      <div className="grid grid-cols-1 md:grid-cols-2 gap-4 mb-4">
        <input
            name="name"
            placeholder="Name"
            value={form.name || ""}
            onChange={handleInput}
            className="p-2 border rounded"
        />
        <input
            name="description"
            placeholder="Description"
            value={form.description || ""}
            onChange={handleInput}
            className="p-2 border rounded"
        />
        <input
            name="price"
            type="number"
            step="0.01"
            placeholder="Price"
            value={form.price || ""}
            onChange={handleInput}
            className="p-2 border rounded"
        />

        {/* Available Until with label */}
        <div className="flex flex-col">
            <label htmlFor="availableUntil" className="text-sm text-gray-600 mb-1">
            Available Until:
            </label>
            <input
            id="availableUntil"
            name="availableUntil"
            type="date"
            min={new Date().toISOString().split("T")[0]}
            value={form.availableUntil || ""}
            onChange={handleInput}
            className="p-2 border rounded"
            />
        </div>

        <input
            name="stockQuantity"
            type="number"
            placeholder="Stock Quantity"
            value={form.stockQuantity || ""}
            onChange={handleInput}
            className="p-2 border rounded"
        />
        </div>


      <button onClick={handleCreate} className="bg-green-600 text-white px-6 py-2 rounded hover:bg-green-700 mb-8">
        ‚ûï Create Product
      </button>

      {/* Update stock section */}
      <div className="bg-white p-4 rounded shadow mb-8 max-w-xl">
        <h3 className="font-bold mb-2">üõ†Ô∏è Update Product Quantity</h3>
        <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
          <input type="number" placeholder="Product ID" value={updateId} onChange={(e) => setUpdateId(e.target.value)} className="p-2 border rounded" />
          <input type="number" placeholder="New Quantity" value={updateQuantity} onChange={(e) => setUpdateQuantity(e.target.value)} className="p-2 border rounded" />
        </div>
        <button onClick={handleUpdateStock} className="mt-4 bg-yellow-500 hover:bg-yellow-600 text-white px-4 py-2 rounded">
          Update Quantity
        </button>
      </div>

      {/* List of all products */}
      <h2 className="text-xl font-bold mb-4">üìã All Products</h2>
      <div className="grid md:grid-cols-2 lg:grid-cols-3 gap-6">
        {products.map((product) => (
          <div key={product.productId} className="bg-white shadow rounded p-4">
            <h3 className="font-semibold">{product.name} #{product.productId}</h3>
            <p className="text-sm text-gray-600">{product.description}</p>
            <p className="text-blue-600 font-bold mt-1">${product.price.toFixed(2)}</p>
            <p className="text-sm text-gray-500">Stock: {product.stockQuantity}</p>
            <p className="text-sm text-gray-500">Available Until: {product.availableUntil}</p>
            <button
              onClick={() => handleDelete(product.productId)}
              className="mt-4 bg-red-500 hover:bg-red-600 text-white px-4 py-1 rounded"
            >
              üóë Remove
            </button>
          </div>
        ))}
      </div>
    </div>
  );
};

export default ProductCatalogAdmin;
