import React, { useEffect, useState } from "react";
import { getUserOrders, cancelOrder, getCurrentUserId } from "../services/api";

const CancelOrder = () => {
  const [orders, setOrders] = useState([]);
  const [orderIdToCancel, setOrderIdToCancel] = useState("");
  const [message, setMessage] = useState("");

  useEffect(() => {
    const fetchOrders = async () => {
      try {
        const userId = getCurrentUserId();
        const res = await getUserOrders(userId);
        setOrders(res.data);
      } catch (err) {
        setMessage("❌ Error fetching orders.");
      }
    };

    fetchOrders();
  }, []);

  const handleCancel = async () => {
    if (!orderIdToCancel) return setMessage("Please enter a valid Order ID.");

    try {
      await cancelOrder(orderIdToCancel);
      setMessage(`✅ Order ${orderIdToCancel} cancelled successfully.`);
      setOrderIdToCancel("");
      setOrders(prev => prev.filter(o => o.orderId !== parseInt(orderIdToCancel)));
    } catch (err) {
      setMessage("❌ Failed to cancel order. It may be already completed or cancelled.");
    }
  };

  return (
    <div className="min-h-screen bg-gray-50 p-6">
      <h1 className="text-3xl font-bold text-center mb-6">❌ Cancel Order</h1>

      {message && <p className="text-center text-red-600 mb-4">{message}</p>}

      <div className="overflow-x-auto mb-6">
        <table className="w-full table-auto bg-white shadow rounded">
          <thead className="bg-red-100">
            <tr>
              <th className="px-4 py-2">Order ID</th>
              <th className="px-4 py-2">Total</th>
              <th className="px-4 py-2">Status</th>
              <th className="px-4 py-2">Date</th>
            </tr>
          </thead>
          <tbody>
            {orders.map(order => (
              <tr key={order.orderId} className="border-b">
                <td className="px-4 py-2">{order.orderId}</td>
                <td className="px-4 py-2">${order.totalPrice.toFixed(2)}</td>
                <td className="px-4 py-2">{order.status.name}</td>
                <td className="px-4 py-2">{new Date(order.createdAt).toLocaleDateString()}</td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>

      <div className="max-w-md mx-auto bg-white shadow-md rounded p-4 space-y-4">
        <input
          type="number"
          className="w-full border px-3 py-2 rounded"
          placeholder="Enter Order ID to cancel"
          value={orderIdToCancel}
          onChange={e => setOrderIdToCancel(e.target.value)}
        />
        <button
          onClick={handleCancel}
          className="w-full bg-red-500 text-white py-2 px-4 rounded hover:bg-red-600"
        >
          Cancel Selected Order
        </button>
      </div>
    </div>
  );
};

export default CancelOrder;
