import React, { useEffect, useState } from "react";
import { getUserOrders, getCurrentUserId, cancelOrder } from "../services/api";
import { useNavigate } from "react-router-dom";

const MyOrders = () => {
  const [orders, setOrders] = useState([]);
  const [error, setError] = useState("");
  const navigate = useNavigate();

  useEffect(() => {
    const fetchOrders = async () => {
      try {
        const userId = getCurrentUserId();
        const response = await getUserOrders(userId);
        setOrders(response.data);
      } catch (err) {
        setError("❌ Error fetching orders: " + err.message);
      }
    };

    fetchOrders();
  }, []);

  const handleCancelOrder = async (orderId) => {
    try {
      await cancelOrder(orderId);
      setOrders((prevOrders) =>
        prevOrders.filter((order) => order.orderId !== orderId)
      );
    } catch (err) {
      setError("❌ Error canceling order: " + err.message);
    }
  };

  return (
    <div className="min-h-screen bg-gray-100 p-6">
      <button
        className="mb-4 px-4 py-2 bg-blue-500 text-white rounded"
        onClick={() => navigate("/dashboard")}
      >
        🔙 Back to Dashboard
      </button>

      <h1 className="text-3xl font-bold mb-6 text-center">📄 My Orders</h1>

      {error && <p className="text-red-600 text-center mb-4">{error}</p>}

      {orders.length === 0 ? (
        <p className="text-center text-gray-600">No orders found.</p>
      ) : (
        <div className="overflow-x-auto">
          <table className="w-full table-auto bg-white shadow-md rounded">
            <thead className="bg-blue-100">
              <tr>
                <th className="px-4 py-2 text-left">Order ID</th>
                <th className="px-4 py-2 text-right">Total Price</th>
                <th className="px-4 py-2 text-center">Status</th>
                <th className="px-4 py-2 text-left">Order Date</th>
                <th className="px-4 py-2 text-center">Actions</th>
              </tr>
            </thead>
            <tbody>
              {orders.map((order) => (
                <tr key={order.orderId} className="border-b">
                  <td className="px-4 py-2">{order.orderId}</td>
                  <td className="px-4 py-2 text-right">${order.totalPrice.toFixed(2)}</td>
                  <td className="px-4 py-2 text-center">{order.status.name}</td>
                  <td className="px-4 py-2">{new Date(order.createdAt).toLocaleDateString()}</td>
                  <td className="px-4 py-2 text-center">
                    {order.status.name === "Pending" && (
                      <button
                        className="px-4 py-2 bg-red-500 text-white rounded"
                        onClick={() => handleCancelOrder(order.orderId)}
                      >
                        Cancel Order
                      </button>
                    )}
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      )}
    </div>
  );
};

export default MyOrders;
